$(document).ready(function(){
    $("#containerPesquisar").hide();
    $("#containerInserir").hide();
    $("#containerAlterar").hide();
    $("#containerDeletar").hide();
   $("#pesquisar").click(function(){
      $("#bemvindo").hide();
      $("#containerAlterar").hide();
      $("#containerInserir").hide();  
      $("#containerDeletar").hide(); 
      $("#containerPesquisar").show(); 
   });
   $("#inserir").click(function(){
      $("#bemvindo").hide();  
      $("#containerAlterar").hide();
      $("#containerInserir").show();  
      $("#containerDeletar").hide(); 
      $("#containerPesquisar").hide(); 
   });
   $("#alterar").click(function(){
      $("#bemvindo").hide();  
      $("#containerAlterar").show();
      $("#containerInserir").hide();  
      $("#containerDeletar").hide(); 
      $("#containerPesquisar").hide(); 
   });
   $("#deletar").click(function(){
      $("#bemvindo").hide();  
      $("#containerAlterar").hide();
      $("#containerInserir").hide();  
      $("#containerDeletar").show(); 
      $("#containerPesquisar").hide(); 
   });
   
   $("#buttonPesquisa").click(function(){
        $.ajax({
            url : "../controller/Controller.php",
            type : 'post',
            data : {
               nome : $("#nome").val(),
               email : $("#email").val(),
               senha: $("#senha").val()
            }
        })
   });
   $("#buttonInserir").click(function(){
        $.ajax({
            url : "../../controller/Controller.php",
            type : 'post',
            data : {
               nome : $("#nome").val(),
               email : $("#email").val(),
               senha: $("#senha").val()
            }
        })
   });
   $("#buttonAlterar").click(function(){
        $.ajax({
            url : "../../controller/Controller.php",
            type : 'post',
            data : {
               nome : $("#nome").val(),
               email : $("#email").val(),
               senha: $("#senha").val()
            }
        })
   });
   $("#buttonDeletar").click(function(){
        $.ajax({
            url : "../../controller/Controller.php",
            type : 'post',
            data : {
               nome : $("#nome").val(),
               email : $("#email").val(),
               senha: $("#senha").val()
            }
        })
   });
});
